int lengthOfLongestSubstring(char *s) {

    //char *letters=calloc(26, sizeof(char));
    int *pos=malloc(256*sizeof(int));
    memset(pos, -1, 256*sizeof(int));
    int strL=strlen(s);
    int len=0;
    int max=0;
    int curBegin=0;

    for(int i=0; i<strL; i++) {
        int index=s[i];

        if(pos[index]<curBegin) {

            len++;
        } else {
            len=len-(pos[index]-curBegin);
            curBegin=pos[index]+1;
        }

        printf("c: %c, len: %d, curBegin: %d, pos[index]: %d\n",
               s[i], len, curBegin, pos[index]);
        pos[index]=i;

        max=(len>max)?len:max;
    }

    return max;
}
